const windowKey = msg.window;
const labels = { morning: "08-12", afternoon: "12-17", evening: "17-22" };
if (!windowKey || !labels[windowKey]) {
    node.status({ fill: "red", shape: "ring", text: "Unknown window" });
    return null;
}
flow.set("window_" + windowKey + "_used", 0);
node.status({ fill: "blue", shape: "dot", text: labels[windowKey] + " reset" });
return null;

const portion = Number(msg.payload);
if (Number.isNaN(portion) || portion <= 0) {
    node.status({ fill: "red", shape: "ring", text: "Invalid portion" });
    return null;
}
flow.set('portion_size', portion);
node.status({ fill: "green", shape: "dot", text: `Portion ${portion} g` });
return null;

const portion = Number(flow.get('portion_size') ?? 8);
const threshold = 4;
const rawValue = Number(msg.payload);
if (Number.isNaN(rawValue)) {
    node.status({ fill: "red", shape: "ring", text: "No reading" });
    return null;
}
if (rawValue >= threshold) {
    node.status({ fill: "grey", shape: "dot", text: `Bowl has ${rawValue} g` });
    return null;
}
const now = new Date();
const minutes = now.getHours() * 60 + now.getMinutes();
const windows = [
    { key: 'morning', label: '08-12', start: 480, end: 720, max: 24 },
    { key: 'afternoon', label: '12-17', start: 720, end: 1020, max: 24 },
    { key: 'evening', label: '17-22', start: 1020, end: 1320, max: 40 }
];
const active = windows.find(w => minutes >= w.start && minutes < w.end);
if (!active) {
    node.status({ fill: "grey", shape: "ring", text: "Outside window" });
    return null;
}
const usedKey = `window_${active.key}_used`;
const used = Number(flow.get(usedKey) ?? 0);
const lastFeed = Number(flow.get('last_feed_ts') ?? 0);
if (Date.now() - lastFeed < 10 * 60 * 1000) {
    node.status({ fill: "grey", shape: "ring", text: "Fed less than 10 min ago" });
    return null;
}
if (used + portion > active.max) {
    node.status({ fill: "yellow", shape: "ring", text: `${active.label} limit ${used}/${active.max} g` });
    return null;
}
flow.set(usedKey, used + portion);
flow.set('last_feed_ts', Date.now());
msg.payload = {
    window: active.key,
    label: active.label,
    portion,
    total_used: used + portion,
    limit: active.max,
    time: now.toISOString()
};
node.status({ fill: "green", shape: "dot", text: `${active.label}: +${portion} g (${used + portion}/${active.max})` });
return msg;
